        StorageItem("artillery_2", "Sample Artillery 2", "placeholder_image_url", "Sample Origin", "1941", "WWII", "Sample First Appearance", "Sample brief history for artillery piece 2.", "placeholder_trivia_data", "placeholder_technical_specs", "artillery"),
        StorageItem("artillery_3", "Sample Artillery 3", "placeholder_image_url", "Sample Origin", "1942", "WWII", "Sample First Appearance", "Sample brief history for artillery piece 3.", "placeholder_trivia_data", "placeholder_technical_specs", "artillery")
    )
    
    private fun getAircraftList(): List<StorageItem> = listOf(
        StorageItem("aircraft_1", "Sample Aircraft 1", "placeholder_image_url", "Sample Origin", "1940", "WWII", "Sample First Appearance", "Sample brief history for aircraft 1.", "placeholder_trivia_data", "placeholder_technical_specs", "aircraft"),
        StorageItem("aircraft_2", "Sample Aircraft 2", "placeholder_image_url", "Sample Origin", "1941", "WWII", "Sample First Appearance", "Sample brief history for aircraft 2.", "placeholder_trivia_data", "placeholder_technical_specs", "aircraft"),
        StorageItem("aircraft_3", "Sample Aircraft 3", "placeholder_image_url", "Sample Origin", "1942", "WWII", "Sample First Appearance", "Sample brief history for aircraft 3.", "placeholder_trivia_data", "placeholder_technical_specs", "aircraft")
